let imagesSlider = ['1.png', '2.png', '3.png'];

let slider = document.querySelector('#slider');
let topSlider = slider.querySelector('top.slider');

let i = 1;
topSlider = 'imagesSlider/' + imagesSlider[0];

window.setInterval(function() {

    topSlider = 'imagesSlider/' + imagesSlider[i];

    i++;

    if (i == imagesSlider.length) {
        i = 0;
    }
}, 3000);