let images = ['slider_1.png', 'slider_2.png', 'slider_3.png'];

let slider = document.querySelector('#slider');
let img = slider.querySelector('img');

let i = 1;
img.src = 'images/' + images[0];

window.setInterval(function() {

    img.src = 'images/' + images[i];

    i++;

    if (i == images.length) {
        i = 0;
    }
}, 1000);